// ============================================
// Test Script: Factorial Calculator
// ============================================
// This verifies that:
// 1. JavaScript/Node execution works correctly
// 2. Recursive functions work
// 3. Output is captured from console.log
// ============================================

function factorial(n) {
  if (n <= 1) return 1;
  return n * factorial(n - 1);
}

const n = 10;
const result = factorial(n);
console.log(`Factorial of ${n} is: ${result}`);
console.log(`Verification: 10! = 3628800, Match: ${result === 3628800}`);

// Bonus: FizzBuzz for first 15 numbers
console.log('\nFizzBuzz (1-15):');
for (let i = 1; i <= 15; i++) {
  let output = '';
  if (i % 3 === 0) output += 'Fizz';
  if (i % 5 === 0) output += 'Buzz';
  console.log(output || i);
}
